  if (!movieSearchResults.value) {
    utils.showNotification({ 
      title: "Please select a movie first", 
      type: "warning" 
    });
    return;
  }

  getMovieDetails.trigger({
    onSuccess: (data) => {
      if (data.Response === "False") {
        utils.showNotification({ title: data.Error, type: "error" });
        return;
      }

      try {
        // Add the movie title (this was missing!)
        titleInput.setValue(data.Title || "");

        // Populate all form fields with the selected movie's data
        release_yearInput.setValue(parseInt(data.Year) || null);
        genreInput.setValue(data.Genre || "");
        directorInput.setValue(data.Director || "");
        countryInput.setValue(data.Country || "");
        languageInput.setValue(data.Language || "");
        runtimeInput.setValue(data.Runtime || "");
        plotInput.setValue(data.Plot || "");
        awardsInput.setValue(data.Awards || "");
        poster_urlInput.setValue(data.Poster || "");
        typeInput.setValue(data.Type || "");
        writerInput.setValue(data.Writer || "");
        actorsInput.setValue(data.Actors || "");

        // MPAA rating
        const mpaa = data.Rated;
        if (["G", "PG", "PG-13", "R", "NC-17", "Unrated"].includes(mpaa)) {
          mpaa_ratingInput.setValue(mpaa);
        } else {
          mpaa_ratingInput.setValue("Unrated");
        }

        // External ratings
        const imdbVal = parseFloat(data.imdbRating) || null;
        const metacriticVal = parseInt(data.Metascore) || null;

        imdb_ratingInput.setValue(imdbVal);
        metacritic_scoreInput.setValue(metacriticVal);

        // Rotten Tomatoes rating
        let rtVal = null;
        const rtRating = data.Ratings?.find((r) => r.Source === "Rotten Tomatoes")?.Value;
        if (rtRating) {
          const rtValue = parseInt(rtRating.replace("%", ""));
          rtVal = isNaN(rtValue) ? null : rtValue;
          rt_critic_scoreInput.setValue(rtVal);
        }

        // Calculate star rating
        const ratings = [];
        if (imdbVal) ratings.push(imdbVal / 2.0);
        if (rtVal) ratings.push(rtVal / 20.0);
        if (metacriticVal) ratings.push(metacriticVal / 20.0);

        const starRating = ratings.length > 0 ? ratings.reduce((a, b) => a + b) / ratings.length : null;
        starRatingInput.setValue(starRating);

        utils.showNotification({
          title: `✅ Successfully loaded "${data.Title}" (${data.Year})`,
          type: "success"
        });

        // Hide the dropdown after successful load
        movieSearchResults.setHidden(true);

      } catch (error) {
        console.error("Error populating form:", error);
        utils.showNotification({ title: "Error populating form fields", type: "error" });
      }
    },
    onFailure: (err) => {
      utils.showNotification({ title: "Failed to fetch movie details", type: "error" });
