// Function to clean movie titles for OMDB search
function cleanTitle(title) {
  return title
    .replace(/\(Year \w+\)/g, '') // Remove "(Year Two)" etc.
    .replace(/\s+/g, ' ') // Clean up extra spaces
    .trim();
}

utils.showNotification({ 
  title: `Starting batch fetch for ${movies.length} movies...`, 
  type: "info" 
});

let processed = 0;
let errors = 0;

// Process movies one by one with short delay
for (const movie of movies) {
  const cleanedTitle = cleanTitle(movie.title);
  console.log(`📽️ Processing: ${movie.title} → searching for: ${cleanedTitle}`);

  try {
    await new Promise((resolve) => {
      getMovieData.trigger({
        additionalScope: { currentTitle: cleanedTitle },
        onSuccess: async (data) => {
          if (data.Response === "False") {
            console.log(`❌ Not found: ${cleanedTitle}`);
            errors++;
            resolve();
            return;
          }

          try {
            const rtRatingData = data.Ratings?.find(r => r.Source === "Rotten Tomatoes");
            const rtRating = rtRatingData ? parseInt(rtRatingData.Value.replace("%", "")) : null;

            await updateMovieWithRatings.trigger({
