                movieId: movie.id,
                imdbRating: parseFloat(data.imdbRating) || null,
                metacritic: parseInt(data.Metascore) || null,
                rtRating: rtRating,
                year: parseInt(data.Year) || null,
                genre: data.Genre || null,
                director: data.Director || null,
                plot: data.Plot || null,
                posterUrl: data.Poster || null
              }
            });
            processed++;
            console.log(`✅ Updated: ${movie.title}`);
          } catch (error) {
            errors++;
            console.log(`❌ DB error for: ${movie.title}`, error);
          }
          resolve();
        },
        onFailure: () => {
          errors++;
          console.log(`❌ API error for: ${cleanedTitle}`);
          resolve();
        }
      });
    });

    // Small delay between requests
    await new Promise(resolve => setTimeout(resolve, 300));

  } catch (error) {
    errors++;
    console.error(`Error processing ${movie.title}:`, error);
  }
}

await getMovieCatalog.trigger();

utils.showNotification({ 
