        THEN ROUND((COALESCE(SUM(e.amount), 0) / p.budget_estimated * 100)::numeric, 1)
        ELSE 0 
    END as budget_used_percent,
    COUNT(DISTINCT ph.id) as total_phases,
    COUNT(DISTINCT ph.id) FILTER (WHERE ph.status = 'Completed') as completed_phases,
    CASE 
        WHEN COUNT(DISTINCT ph.id) > 0 
        THEN ROUND((COUNT(DISTINCT ph.id) FILTER (WHERE ph.status = 'Completed')::numeric / COUNT(DISTINCT ph.id) * 100), 0)
        ELSE 0 
    END as progress_percent,
    p.location,
    p.description,
    p.created_at,
    p.updated_at,
    CASE 
        WHEN p.target_end_date < CURRENT_DATE AND p.status IN ('Planning', 'In Progress') 
        THEN true 
        ELSE false 
    END as is_overdue
FROM garden_projects p
LEFT JOIN garden_project_categories pc ON p.category_id = pc.id
LEFT JOIN garden_project_expenses e ON p.id = e.project_id
LEFT JOIN garden_project_phases ph ON p.id = ph.project_id
WHERE 1=1
  AND ({{ !selectProjectStatus.value || selectProjectStatus.value === 'All' }} OR p.status = {{ selectProjectStatus.value }})
  AND ({{ !selectProjectCategory.value || selectProjectCategory.value === 'All' || selectProjectCategory.value === null }} OR p.category_id = {{ parseInt(selectProjectCategory.value) }})
  AND ({{ !searchProjects.value }} OR LOWER(p.name) LIKE LOWER({{ '%' + searchProjects.value + '%' }}) OR LOWER(p.description) LIKE LOWER({{ '%' + searchProjects.value + '%' }}))
GROUP BY p.id, p.name, pc.name, p.status, p.priority, p.start_date, 
         p.target_end_date, p.budget_estimated, p.budget_actual, p.location, 
         p.description, p.created_at, p.updated_at
ORDER BY 
    CASE p.status 
        WHEN 'In Progress' THEN 1 
        WHEN 'Planning' THEN 2 
        WHEN 'On Hold' THEN 3 
        WHEN 'Completed' THEN 4 
        ELSE 5 
    END,
    CASE p.priority
        WHEN 'High' THEN 1
        WHEN 'Medium' THEN 2
        WHEN 'Low' THEN 3
        ELSE 4
